import React, { useState, useRef } from "react";
import { FaArrowCircleDown } from "react-icons/fa";

function App() {
  const uploadRef = useRef(null);
  const resultRef = useRef(null);

  const scrollToUpload = () => {
    uploadRef.current.scrollIntoView({ behavior: "smooth" });
  };

  const scrollToResult = () => {
    setTimeout(() => {
      resultRef.current.scrollIntoView({ behavior: "smooth" });
    }, 300);
  };

  const [resumeFile, setResumeFile] = useState(null);
  const [jobDesc, setJobDesc] = useState("");
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [results, setResults] = useState(null);
  const [error, setError] = useState("");
  const [uploaded, setUploaded] = useState(false);
  const [uploadSuccess, setUploadSuccess] = useState(false);

  // Sample job descriptions for quick testing
  const samples = [
    {
      name: "Data Scientist Demo",
      jobDesc: "machine learning, python, data analysis",
    },
    {
      name: "Frontend Developer Demo",
      jobDesc: "react, javascript, html, css",
    },
    {
      name: "Backend Developer Demo",
      jobDesc: "node.js, express, mongodb, api",
    },
  ];

  const applySample = (sample) => {
    setJobDesc(sample.jobDesc);
  };

  const handleFileChange = (e) => {
    setResumeFile(e.target.files[0]);
    setUploaded(false);
    setResults(null);
    setError("");
  };

  const handleSubmit = async () => {
    if (!resumeFile || !jobDesc.trim()) {
      setError("Please upload resume and enter job description.");
      return;
    }
    setUploading(true);
    setLoading(true);
    setProgress(0);
    setError("");
    setResults(null);

    // Simulate upload progress
    const progressInterval = setInterval(() => {
      setProgress((oldProg) => {
        if (oldProg >= 90) {
          clearInterval(progressInterval);
          return oldProg;
        }
        return oldProg + 10;
      });
    }, 200);

    try {
      const formData = new FormData();
      formData.append("resume", resumeFile);
      formData.append("job_description", jobDesc);

      const response = await fetch("http://127.0.0.1:5000/analyze", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error("Server error");

      const data = await response.json();
      setUploadSuccess(true);
      setResults(data);
      scrollToResult();
    } catch (err) {
      setError(err.message || "Failed to analyze");
      setUploadSuccess(false);
    } finally {
      setUploading(false);
      setLoading(false);
      setProgress(100);
      setTimeout(() => setProgress(0), 600);
      setUploaded(true);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-indigo-700 via-blue-700 to-indigo-800 flex flex-col items-center">
      {/* Landing Section */}
      <section className="h-screen flex flex-col items-center justify-center text-white px-6">
        <h1 className="text-6xl font-extrabold mb-2 tracking-tight">
          JobFit AI
        </h1>
        <p className="text-xl mb-8 max-w-xl text-center">
          Upload your resume and job description — get fast, smart job fit
          insights!
        </p>
        <div className="bg-white bg-opacity-20 rounded-xl p-6 mb-8 shadow-lg flex flex-col items-center text-lg max-w-xl">
          <ul>
            <li className="mb-2">✔ Suitable / Not Suitable screening</li>
            <li className="mb-2">
              ✔ Role classification (e.g. Data Scientist, Backend Dev, etc.)
            </li>
            <li className="mb-2">✔ Match score for key skills</li>
          </ul>
        </div>
        <button
          onClick={scrollToUpload}
          aria-label="Scroll to upload section"
          className="animate-bounce text-6xl mt-4"
          title="Scroll down to upload"
        >
          <FaArrowCircleDown />
        </button>
      </section>

      {/* Upload Section */}
      <section
        ref={uploadRef}
        className="w-full max-w-3xl bg-white rounded-xl shadow-2xl px-10 py-12 -mt-40 flex flex-col items-center z-10"
      >
        <h2 className="text-4xl font-semibold mb-8 text-center">
          Upload and Analyze
        </h2>

        <div className="mb-6 w-full">
          <label className="block mb-3 font-medium text-gray-800 text-lg">
            Upload Resume (PDF)
          </label>
          <input
            type="file"
            accept="application/pdf"
            onChange={handleFileChange}
            className="w-full border border-gray-300 rounded px-4 py-3 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          {resumeFile && (
            <p className="mt-2 text-sm text-gray-600">
              Selected File:{" "}
              <span className="font-semibold">{resumeFile.name}</span>
            </p>
          )}
        </div>

        <div className="mb-6 w-full">
          <label className="block mb-3 font-medium text-gray-800 text-lg">
            Job Description
          </label>
          <textarea
            rows={5}
            value={jobDesc}
            onChange={(e) => setJobDesc(e.target.value)}
            className="w-full border border-gray-300 rounded px-4 py-3 resize-none focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="Paste job description here..."
          />
        </div>

        <button
          onClick={handleSubmit}
          disabled={loading || uploading}
          className={`w-full py-4 rounded font-semibold text-white bg-blue-600 hover:bg-blue-700 disabled:opacity-50 transition`}
          title="Analyze Job Fit"
        >
          {loading
            ? "Analyzing..."
            : uploading
            ? "Uploading..."
            : "Analyze Job Fit"}
        </button>

        {uploading && (
          <div className="w-full bg-gray-200 rounded h-2 mt-3 overflow-hidden">
            <div
              className="bg-blue-600 h-2 transition-all duration-200"
              style={{ width: `${progress}%` }}
            />
          </div>
        )}

        {uploaded && uploadSuccess && (
          <p className="mt-4 text-green-700 text-center font-semibold">
            Upload complete! Scroll down for results.
          </p>
        )}
        {uploaded && !uploadSuccess && error === "" && (
          <p className="mt-4 text-yellow-600 text-center">
            Upload finished. Waiting for result...
          </p>
        )}
        {error && (
          <p className="mt-4 text-red-600 text-center font-semibold">{error}</p>
        )}

        <div className="mt-8 w-full max-w-md mx-auto text-center">
          <h4 className="font-semibold mb-3 text-gray-700">
            Sample Job Descriptions
          </h4>
          <div className="flex justify-center gap-4 flex-wrap">
            {samples.map((sample) => (
              <button
                key={sample.name}
                onClick={() => applySample(sample)}
                className="bg-gray-300 rounded px-4 py-2 hover:bg-gray-400 transition font-medium"
                title={`Apply sample: ${sample.name}`}
              >
                {sample.name}
              </button>
            ))}
          </div>
        </div>
      </section>

      {/* Results Section */}
      {results && (
        <section
          ref={resultRef}
          className="w-full max-w-3xl bg-green-50 rounded-xl shadow-xl px-10 py-10 mt-12 mb-24"
        >
          <h3 className="text-3xl font-semibold mb-6">Results</h3>
          <p className="mb-4 text-lg">
            Binary Classification:{" "}
            <span
              className={
                results.binary_classification === "Suitable"
                  ? "text-green-700 font-bold"
                  : "text-red-700 font-bold"
              }
            >
              {results.binary_classification}
            </span>
          </p>
          <p className="mb-4 text-lg">
            Job Role:{" "}
            <span className="font-bold text-blue-900">{results.job_role}</span>
          </p>
          <p className="text-lg">
            Match Score:{" "}
            <span className="font-bold">{results.match_score}%</span>
          </p>
        </section>
      )}
    </div>
  );
}

export default App;
