import React, { useState, useRef } from "react";

function App() {
  const uploadRef = useRef(null);

  const scrollToUpload = () => {
    uploadRef.current.scrollIntoView({ behavior: "smooth" });
  };

  const [resumeFile, setResumeFile] = useState(null);
  const [jobDesc, setJobDesc] = useState("");
  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState(null);
  const [error, setError] = useState("");

  const handleFileChange = (e) => {
    setResumeFile(e.target.files[0]);
  };

  const handleSubmit = async () => {
    if (!resumeFile || !jobDesc.trim()) {
      setError("Please upload resume and enter job description.");
      return;
    }
    setLoading(true);
    setError("");
    setResults(null);

    try {
      const formData = new FormData();
      formData.append("resume", resumeFile);
      formData.append("job_description", jobDesc);

      const response = await fetch("http://127.0.0.1:5000/analyze", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error("Server error");

      const data = await response.json();
      setResults(data);
    } catch (err) {
      setError(err.message || "Failed to analyze");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <section className="h-screen flex flex-col items-center justify-center bg-gradient-to-b from-blue-600 to-indigo-700 text-white px-6">
        <h1 className="text-5xl font-bold mb-4">JobFit AI</h1>
        <p className="text-xl max-w-xl mb-12 text-center">
          Upload your resume and job description — get fast insights if you fit
          the role!
        </p>
        <button
          onClick={scrollToUpload}
          aria-label="Scroll to upload section"
          className="animate-bounce text-5xl"
        >
          ↓
        </button>
      </section>

      <section
        ref={uploadRef}
        className="max-w-3xl mx-auto p-6 bg-white shadow rounded -mt-24 relative z-10"
      >
        <h2 className="text-3xl font-semibold mb-6 text-center">
          Upload and Analyze
        </h2>

        <div className="mb-4">
          <label className="block mb-2 font-medium">Upload Resume (PDF)</label>
          <input
            type="file"
            accept="application/pdf"
            onChange={handleFileChange}
            className="w-full border p-2 rounded"
          />
        </div>

        <div className="mb-4">
          <label className="block mb-2 font-medium">Job Description</label>
          <textarea
            rows={5}
            value={jobDesc}
            onChange={(e) => setJobDesc(e.target.value)}
            className="w-full border p-2 rounded resize-none"
            placeholder="Paste job description here..."
          />
        </div>

        <button
          onClick={handleSubmit}
          disabled={loading}
          className="w-full bg-blue-600 text-white py-3 rounded font-semibold hover:bg-blue-700 disabled:opacity-50"
        >
          {loading ? "Analyzing..." : "Analyze Job Fit"}
        </button>

        {error && <p className="mt-4 text-red-600 text-center">{error}</p>}

        {results && (
          <div className="mt-8 bg-green-50 p-4 rounded shadow">
            <h3 className="text-xl font-semibold mb-2">Results</h3>
            <p>
              Binary Classification:{" "}
              <strong>{results.binary_classification}</strong>
            </p>
            <p>
              Job Role: <strong>{results.job_role}</strong>
            </p>
            <p>
              Match Score: <strong>{results.match_score}%</strong>
            </p>
          </div>
        )}
      </section>
    </div>
  );
}

export default App;
